class dataBase():
    def connectDB(self):
        self.conn = (
            'Driver={SQL Server};'
            'Server=DESKTOP-C337USR\SQLEXPRESS;'
            'Database=Controle_de_Estoque;'
        )
        
        self.connect = pyodbc.connect(self.conn)
        self.cursor = self.connect.cursor()
        print('Conex√£o feita com suscesso!')

    def disconnectDB(self):
        self.connect.close()
